import React, { Component, useState } from 'react'
import Router from 'next/router';
import cookies from 'next-cookies';
import cookie from 'js-cookie';
import { Link } from '@material-ui/core';
import fetch from 'isomorphic-unfetch';
import styled from 'styled-components';
import Typography from '@material-ui/core/Typography';
import ExpandMoreOutlinedIcon from '@material-ui/icons/ExpandMoreOutlined';
import CreateIcon from '@material-ui/icons/Create';
import AssignmentIcon from '@material-ui/icons/Assignment';
import ExitToAppIcon from '@material-ui/icons/ExitToApp';
import CheckCircleIcon from '@material-ui/icons/CheckCircle';
import CancelIcon from '@material-ui/icons/Cancel';
import { withAuthSync, logout, login } from '../utils/auth'
import HeroSection from '../components/HeroSection';
import { Button } from '../components/Button';
import { StyledBanner, StyledContainer } from '../components/styles/PageStyles';
import { GetForm } from '../components/forms/FormActions';
import { SendFile } from '../components/forms/Abstract';
import Popup from '../components/Popup';
import OneForm from '../components/forms/OneForm';


const StyledPage = styled.div`
    display: flex;

    flex-direction: column;
    align-self: center;
    width: ${props => props.theme.minWidth};
    background-color: ${props => props.theme.white};
    margin-top: 7rem;
    .authors{
        color: ${props => props.theme.primary};
        font-family: ${props => props.theme.MPLight};
        font-size: 2rem;
    &-title{
        color: ${props => props.theme.primary};
        font-family: ${props => props.theme.MPSemibold};
        font-size: 4.4rem;

    }
    &_sub-title{
        color: ${props => props.theme.primary};
        font-size: 3.5rem;
        font-family: ${props => props.theme.MPLightIt};
        padding-bottom: .5rem;
    }
    &-bold{
        color: ${props => props.theme.primary};
        font-family: ${props => props.theme.MPBold};
    }
    &-it{
        font-family: ${props => props.theme.MPLightIt};
    }
    &_action-box{
        display: flex;
        justify-content: space-between;
        margin-bottom: 3rem;
    }
}
`;

const StyledBox = styled.div`
    display: flex;
    justify-content: space-between;
    min-height: 15rem;
    background-color: #D4DDE8;
    padding: 1rem;
    margin-bottom: 2rem;

`;

const StyledActionArea = styled.div`
    flex: 1 1 33.33%;
    display: flex;
    flex-flow: column;
    justify-content: space-between;
    align-items: flex-start;

    .action-paper{
        color: ${props => props.theme.black};
        font-family: ${props => props.theme.MPBold};
        font-size: 2rem;
    }
`;

const StyledInfoArea = styled.div`
    flex: 1 1 66.66%;
    display: flex;
    flex-flow: column;
    justify-content: space-between;
    align-items: flex-start;

    .paper-title{
    font-size: 2rem;
    color: ${props => props.theme.black};

    }
    .paper-type{
    font-size: 2rem;
    color: ${props => props.theme.black};
    font-family: ${props => props.theme.MPLightIt};

    }
    .paper_task{
    color: ${props => props.theme.black};
    font-size: 1.5rem;

    &-box{
        display: flex;
        align-items: flex-start;
    }}
`;


const FilledBanner = () => (

    <StyledBanner>
        <StyledContainer >
            <Typography className="banner-heading"  >Welcome to the Author's Area</Typography>
            <Typography className="banner-sub" sfs="3rem">Submit your review ready papers by 26 January 2020</Typography>
            <div className="banner_bottom">
                <ExpandMoreOutlinedIcon className="banner_bottom-icon" />
                <Typography varient="h3" className="banner_bottom-details">Submit your review ready papers by 26 January 2020</Typography>
            </div>
        </StyledContainer>
    </StyledBanner>
)



// * NEED TO SORT OUT DYNAMIC ROUTING FOR EDITING A PAPER
const Abstract = ({papers}) => (
papers.map((paper, i) => (
    <StyledBox>
        <StyledActionArea>
            <Typography gutterBottom className="action-paper">{paper.papercode}</Typography>
            <Link >
            <Button
                bcolor="#134381"
                background="#134381"
                br="100rem"
                padding="0.5rem 2rem"
                style={{ margin: ".5rem 0" }}
                fontSize="1.7rem"
                onClick={() => GetForm(`https://prelude.eurobrake.net/edit/${paper.papercode}`)}
            >
                <CreateIcon style={{ fontSize: '3rem', marginRight: '1rem' }} />
                EDIT PAPER
            </Button>
            </Link>
            <Button
                bcolor="#134381"
                background="#134381"
                br="100rem"
                padding="0.5rem 2rem"
                style={{ margin: ".5rem 0" }}
                fontSize="1.7rem"
            >
                <CancelIcon style={{ fontSize: '3rem', marginRight: '1rem' }} />
                WITHDRAW
            </Button>
        </StyledActionArea>
        <StyledInfoArea>
