                            <label
                                htmlFor="label"
                                className="form-label">
                                Password:
                        </label>
                            <Field
                                type="password"
                                className="form-input"
                                onClick={handleChange}
                                value={values.new_password}
                                style={{ color: '#134381' }}
                                name="new_password"
                                component={TextField}
                            />
                        </div>

                        <div className="form-field">
                            <label
                                htmlFor="confirm_password"
                                className="form-label">
                                Confirm Password:
                        </label>
                            <Field
                                type="password"
                                className="form-input"
                                onClick={handleChange}
                                value={values.confirm_password}
                                style={{ color: '#134381' }}
                                name="confirm_password"
                                component={TextField}
                            />
                        </div>

                        <Button 
                            onClick={() => allTouched()}
                            bcolor="#134381"
                            background="#134381"
                            br="100rem"
                            style={{ margin: "3rem" }}
                            padding=".5rem 4rem"
                            fontSize="1.7rem">
                            Reset
                        </Button>
                    </StyledForm>
                )
            }}
        </Formik>
    )
};


const emptyInitial = {
    new_password: '',
    confirm_password: '',
};
