                throw error
            }
        } catch (error) {
            console.error(
                'Failed to send form, please try again', error
            )
            return data
         }
        }
    if(FT === 'withdraw' ){
        console.log({url});
        try {
            const response = await fetch(url, {
                method: 'POST', 
                credentials: 'include',
                headers: {
                    'Content-Type': 'application/json'
                },
            });

            const data = await response.json()

            if (data.status === 'success') {
            console.log('getForm', data);
            return data

