import React, { Component, useState, useRef, useEffect } from 'react'
import { Formik, Form, Field, FieldArray, FastField, ErrorMessage, } from 'formik';
import { TextField, RadioGroup, Select, Checkbox } from 'formik-material-ui';
import { AddCircle, RemoveCircle, ArrowUpwardRounded, ArrowDownwardRounded, } from '@material-ui/icons';
import cookie from 'js-cookie';
import { Button } from '../Button';
import { Typography } from '@material-ui/core';
import {titles, countries} from './FormSelects';
import {AbstractSchema} from './FormControl';
import {StyledForm} from './Formstyles';


export const AbstractForm = (editPaper = false) => {
    const [presenter, setPresenter] = useState('');
    // if edit = true send back a preset form if not send a blank
    console.log({editPaper})
    useEffect(() => {

        console.log('presenter is changing')

    }, [presenter])

    return (
        <Formik
            initialValues={editPaper ? presets : emptyInitial }
            onSubmit={(values, actions) => {
                setTimeout(() => {
                    alert(JSON.stringify(values, null, 2));
                    actions.setSubmitting(false);
                }, 1000)
            }}
            validationSchema={AbstractSchema}
            enableReinitialize
        >
            {({ values, isSubmitting, isValidating, handleChange, setFieldValue }) => {

                const allNo = ({name}) => {
                    if(process.browser){
                    const radios = document.querySelectorAll('input[name*="is_presenting_author"]');
                    {/* console.log(name) */}
                    radios.forEach(input => {
                        input.name !== name && setFieldValue(input.name, input.value) &&
                        console.log('gonna change...', input.name, 'cos it aint the same as...', name, ) 
                    })}
                }


                const onPresenterChange = (event) => {
                    const { target } = event;
                    const value = target.value;
                    const name = target.name;
                    value === 'no' ? setPresenter('') : setPresenter(name);
                    setFieldValue(name, value);
                    allNo({ name });
                    return console.log({ values })
                }


                return (
                    <StyledForm>
                        <Typography className="form-title">Primary Author</Typography>
                        <div className="form-field">
                            <label
                                htmlFor="label"
                                className="form-label">
                                Title
                        </label>
                            <Field
                                className="form-input"
                                onClick={handleChange}
                                value={values.author_title}
                                style={{ color: '#134381' }}
                                name="author_title"
                                component={Select}
                            >
                                {titles.map((option, i) =>
                                    <option key={i} style={{ fontSize: '1.5rem', cursor: 'pointer' }} value={option.value}>{option.label}</option>)}
                            </Field>
                        </div>
                        <div className="form-field">
                            <label
                                htmlFor="label"
                                className="form-label">
                                First Name:
                        </label>
                            <Field
                                className="form-input"
                                onClick={handleChange}
                                value={values.author_firstname}
                                style={{ color: '#134381' }}
                                name="author_firstname"
                                component={TextField}
                            />
                        </div>
                        <div className="form-field">
                            <label
                                htmlFor="label"
                                className="form-label">
                                Last Name:
                        </label>
                            <Field
                                className="form-input"
                                onClick={handleChange}
                                value={values.author_lastname}
                                style={{ color: '#134381' }}
                                name="author_lastname"
                                component={TextField}
                            />
                        </div>
                        <div className="form-field">
                            <label
                                htmlFor="label"
                                className="form-label">
                                Email:
                        </label>
                            <Field
                                className="form-input"
                                onClick={handleChange}
                                value={values.author_email}
                                style={{ color: '#134381' }}
                                name="author_email"
                                component={TextField}
                            />
                        </div>
                        <div className="form-field">
                            <label
                                htmlFor="label"
                                className="form-label">
                                Job Title:
                        </label>
                            <Field
                                className="form-input"
                                onClick={handleChange}
                                value={values.author_jobtitle}
                                style={{ color: '#134381' }}
                                name="author_jobtitle"
                                component={TextField}
                            />
                        </div>
                        <div className="form-field">
                            <label
                                htmlFor="label"
                                className="form-label">
                                Company:
                        </label>
                            <Field
                                className="form-input"
                                onClick={handleChange}
                                value={values.author_company}
                                style={{ color: '#134381' }}
                                name="author_company"
                                component={TextField}
                            />
                        </div>
                        <div className="form-field">
                            <label
                                htmlFor="label"
                                className="form-label">
                                Address:
                        </label>
                            <Field
                                className="form-input"
                                onClick={handleChange}
                                value={values.author_address1}
                                style={{ color: '#134381' }}
                                name="author_address1"
                                component={TextField}
                            />
                        </div>
                        <div className="form-field">
                            <label
                                htmlFor="label"
                                className="form-label">

                            </label>
                            <Field
                                className="form-input"
                                onClick={handleChange}
                                value={values.author_address2}
                                style={{ color: '#134381' }}
                                name="author_address2"
                                component={TextField}
                            />
                        </div>
                        <div className="form-field">
                            <label
                                htmlFor="label"
                                className="form-label">

                            </label>
                            <Field
                                className="form-input"
                                onClick={handleChange}
                                value={values.author_address3}
                                style={{ color: '#134381' }}
                                name="author_address3"
                                component={TextField}
                            />
                        </div>
                        <div className="form-field">
                            <label
                                htmlFor="label"
                                className="form-label">
                                City:
                        </label>
                            <Field
                                className="form-input"
                                onClick={handleChange}
                                value={values.author_city}
                                style={{ color: '#134381' }}
                                name="author_city"
                                component={TextField}
                            />
                        </div>
                        <div className="form-field">
                            <label
                                htmlFor="label"
                                className="form-label">
                                State/county/province:
                        </label>
                            <Field
                                className="form-input"
                                onClick={handleChange}
                                value={values.author_scp}
                                style={{ color: '#134381' }}
                                name="author_scp"
                                component={TextField}
                            />
                        </div>
                        <div className="form-field">
                            <label
                                htmlFor="label"
                                className="form-label">
                                Postal/Zip code:
                        </label>
                            <Field
                                className="form-input"
                                onClick={handleChange}
                                value={values.author_postal}
                                style={{ color: '#134381' }}
                                name="author_postal"
                                component={TextField}
                            />
                        </div>
                        <div className="form-field">
                            <label
                                htmlFor="label"
                                className="form-label">
                                Country
                        </label>
                            <Field
                                className="form-input"
                                onClick={handleChange}
                                value={values.author_country}
                                style={{ color: '#134381' }}
                                name="author_country"
                                component={Select}
                            >
                                {countries.map((option, i) =>
                                    <option key={i} style={{ fontSize: '1.5rem', cursor: 'pointer' }} value={option.value}>{option.label}</option>)}
                            </Field>
                        </div>
                        <div className="form-field">
                            <label
                                htmlFor="label"
                                className="form-label">
                                Phone:
                        </label>
                            <Field
                                className="form-input"
                                onClick={handleChange}
                                value={values.author_phone}
                                style={{ color: '#134381' }}
                                name="author_phone"
                                component={TextField}
                            />
                        </div>
                        <div className="form-field">
                            <label
                                htmlFor="label"
                                className="form-label">
                                Is this the presenting author?
                        </label>
                            <Field
                                className="form-radio"
                                value={values.is_presenting_author}
                                style={{ color: '#134381' }}
                                name="is_presenting_author"
                                component={RadioGroup}
                            >
                                <label
                                    style={{ margin: '1rem' }}
                                    htmlFor="label"
                                    className="form-label">
                                    Yes
                                        <input
                                        checked={presenter === 'is_presenting_author'}
                                        onChange={(event) => {
                                            onPresenterChange(event)
                                        }
                                        }
                                        type="radio"
                                        name="is_presenting_author"
                                        value="yes"
                                    />
                                </label>
                                <label
                                    style={{ margin: '1rem' }}
                                    htmlFor="label"
                                    className="form-label">
                                    No
                                        <input
                                        onChange={(event) => {
                                            onPresenterChange(event)
                                        }
                                        }
                                        checked={presenter !== 'is_presenting_author'}
                                        type="radio"
                                        name="is_presenting_author"
                                        value="no"
                                    />
                                </label>
                            </Field>
                        </div>

                        <Typography className="form-title">Secondary Author(s)</Typography>

                            <FieldArray name={`secondary_authors`} >
                                {({ swap, push, remove, setSubmitting }) => (
                                    values.secondary_authors &&
                                    values.secondary_authors.length > 0 &&
                                    (
                                        values.secondary_authors.map((authors, index) => (
                                            <div key={index} className="form-secondary">
                                                <div className="form-field">
                                                    <label
                                                        htmlFor="label"
                                                        className="form-label">
                                                        Title:
                                                    </label>
                                                    <Field
                                                        className="form-input"
                                                        onClick={handleChange}
                                                        value={`secondary_authors[${index}].title`}
                                                        style={{ color: '#134381' }}
                                                        name={`secondary_authors[${index}].title`}
                                                        component={TextField}
                                                    />
                                                </div>
                                                <div className="form-field">
                                                    <label
                                                        htmlFor="label"
                                                        className="form-label">
                                                        First Name:
                                                    </label>
                                                    <Field
                                                        className="form-input"
                                                        onClick={handleChange}
                                                        value={`secondary_authors[${index}].firstname`}
                                                        style={{ color: '#134381' }}
                                                        name={`secondary_authors[${index}].firstname`}
                                                        component={TextField}
                                                    />
                                                </div>
                                                <div className="form-field">
                                                    <label
                                                        htmlFor="label"
                                                        className="form-label">
                                                        Last Name:
                                                    </label>
                                                    <Field
                                                        className="form-input"
                                                        onClick={handleChange}
                                                        value={`secondary_authors[${index}].lastname`}
                                                        style={{ color: '#134381' }}
                                                        name={`secondary_authors[${index}].lastname`}
                                                        component={TextField}
                                                    />
                                                </div>
