import React from 'react';
import styled from 'styled-components';
import Typography from '@material-ui/core/Typography';
import CountDownBanner from '../components/CountDownBanner';


const StyledHero = styled.div`
    height: 100vh;  
    display: flex;
    flex-flow: column;

    img{
        flex: 1;
        object-fit: cover;
    }


`;

const StyledText = styled(Typography)`


&&.MuiTypography-body1 {
        font-size: 11rem;
        font-family:${props => props.theme.MPBlack};

    font-weight: 400;
    /* line-height: 1.5; */
    letter-spacing: 0.00938em;
}
   align-self: center;
        max-width: ${props => props.max ? props.max : props.theme.maxWidth};
        position: absolute;
        line-height: 1;
        top: ${props => props.t ? props.t : '20rem'};
        color: ${props => props.theme.white};
        text-align: left;
        text-shadow: 0px 3px 50px #0000008C;

`;

export default function HeroSection(props) {
    const { t, max } = props;
    console.log(props)
    return (
        <StyledHero>
            <img src={props.src ? props.src : "/images/pic1.png"} alt="EuroBrake Greating" />
            <StyledText t="16rem" max="65%">
                {props.children}
            </StyledText>
            {props.banner ? <props.banner /> : <CountDownBanner />}
        </StyledHero>
    )
}
