import React, { Component, useState, useEffect } from 'react'
import { Formik, Form, Field } from 'formik';
import { TextField, Select} from 'formik-material-ui';
import MenuItem from '@material-ui/core/MenuItem';

import CircularProgress from '@material-ui/core/CircularProgress';
import { Button } from '../Button';
import {titles, countries} from './FormSelects';
import {AuthorSchema} from './FormControl';
import {StyledForm} from './Formstyles';
import { SendForm } from './FormActions';
import { Typography } from '@material-ui/core';


export const Authorprofile = (props) => {
    const [Toggle, setToggle] = useState(false);
    const [Status, setStatus] = useState(null);
    const [Loading, setLoading] = useState(false);

    const { presets, csrf, apiUrl, paperId} = props;
    const url = apiUrl;

    return (
        <Formik
            initialValues={ presets || emptyInitial }
            validationSchema={AuthorSchema}
            enableReinitialize
        >
            {({ values, handleChange, validateForm, errors, setFieldTouched, handleSubmit, submitForm}) => {


                   const allTouched = async () => {
                       console.log('touched!')
                       console.log({errors})
                     await Object.keys(values).forEach(key => {   
                        setFieldTouched(key, true)});

                       await validateForm().then(errors => Object.keys(errors).length === 0 && onSubmit())
                }

                   const onSubmit = async () => {
                 await setLoading(true);
                values.__csrf_token = csrf
                  await  console.log('submitting')
                  let res =  await SendForm({values, url, csrf});
                  let data = await  res;
                  let dataStatus= await  data && data.status;
                  setStatus(dataStatus)
                  console.log({data})
                  let result = async () => {
                  await  setLoading(false)
                  await setToggle(true)
                   }    

                return result()

              }

                return (
                    <>
                    <StyledForm>
                        <div className="form-field">
                            <label
                                htmlFor="label"
                                className="form-label">
                                Title
                        </label>
                            <Field
                                className="form-input"
                                onClick={handleChange}
                                value={values.title}
                                style={{ color: '#134381' }}
                                name="title"
                                component={Select}
                            >
                                {titles.map((option, i) =>
                                    <MenuItem key={i} style={{ fontSize: '1.5rem', cursor: 'pointer' }} value={option.value}>{option.label}</MenuItem>)}
                            </Field>
                        </div>
                        <div className="form-field">
                            <label
                                htmlFor="label"
                                className="form-label">
                                First Name:
                        </label>
                            <Field
                                className="form-input"
                                onClick={handleChange}
                                value={values.firstname}
                                style={{ color: '#134381' }}
                                name="firstname"
                                component={TextField}
                            />
                        </div>
                        <div className="form-field">
                            <label
                                htmlFor="label"
                                className="form-label">
                                Last Name:
                        </label>
                            <Field
                                className="form-input"
                                onClick={handleChange}
                                value={values.lastname}
                                style={{ color: '#134381' }}
                                name="lastname"
