import React, { createRef } from 'react';
import styled from 'styled-components';
import Typography from '@material-ui/core/Typography';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import { Button } from './Button';


const StyledPopup = styled.div`
    display: flex;
    flex-direction: column;
    align-self: center;
    width: 50vw;
    background-color: ${props => props.theme.white};
     margin-top: 1rem;

    a{  cursor: pointer;
          color: ${props => props.theme.primary};
        font-family: ${props => props.theme.MPBold};
        font-size: 2rem;

    }
    .popup{
        color: ${props => props.theme.primary};
        font-family: ${props => props.theme.MPLight};
        font-size: 2rem;

    &-title{
        color: ${props => props.theme.primary};
        font-family: ${props => props.theme.MPSemibold};
        font-size: 4.4rem;
        margin-top: 3rem;
        margin-bottom: 3rem;
    }

    &_sub-title{
        color: ${props => props.theme.primary};
        font-size: 3.5rem;
        font-family: ${props => props.theme.MPLightIt};
        padding-bottom: .5rem;
        margin-top: 3rem;
        margin-bottom: 3rem;
    }
    &-bold{
        font-family: ${props => props.theme.MPBold};
    }
    &-it{
        font-family: ${props => props.theme.MPLightIt};
    }
}
`;

export default function InstructionsPopup(props) {
  const [open, setOpen] = React.useState(false);
  // onClose={()=>setOpen(open => !open)}

  return (
    <StyledPopup>
        <a onClick={() => setOpen(open => !open)}>
          {props.children}
        </a>
        <Dialog open={open}  aria-labelledby="dialog-title">
          <DialogTitle id="dialog-title">Assignment of copyright of your Technical Paper</DialogTitle>
          <Typography>THis is a popup</Typography>
          <ul>
            <li><Typography>Ownership of the copyright ensures maximum international protection against infringement</Typography></li>
            <li><Typography></Typography></li>
          </ul>
          <Typography>THis is a popup</Typography>
          <Typography>THis is a popup</Typography>
        </Dialog>
    </StyledPopup>
  );
      {/* <form>
          <DialogTitle id="form-dialog-title">Upload File</DialogTitle>
          <DialogContent>
            <DialogContentText>
              Please do not exceed 50mb file size example
          </DialogContentText>

          </DialogContent>
          <DialogActions>
            <Button onClick={() => setOpen(open => !open)} value="cancel" color="#134381" bcolor="#134381">
              Cancel
          </Button>

          </DialogActions>
      </form> */}
}
