import React from 'react'
import styled from 'styled-components';
import ReactPlayer from 'react-player'
import Link from 'next/link';
import Typography from '@material-ui/core/Typography';
import HeroSection from '../components/HeroSection';
import StopIcon from '@material-ui/icons/Stop';
import {Button} from '../components/Button';
import {Quote2, MailIcon, PhoneIcon} from '../components/Icons';


const StyledPage = styled.div`
    display: flex;
    flex-direction: column;
    align-self: center;
    width: ${props => props.max || props.theme.minWidth};
    background-color: ${props => props.theme.white};
    /* margin-top: 7rem; */
      @media only screen 
  and (max-device-width: 768px) 
  and (-webkit-min-device-pixel-ratio: 2) { 
    width: ${props => props.theme.mobileWidth};    
}

    .Esop{
        color: ${props => props.theme.primary};
        font-family: ${props => props.theme.MPLight};
        font-size: 2rem;
    &-title{
        color: ${props => props.theme.primary};
        font-family: ${props => props.theme.MPSemibold};
        font-size: 4.4rem;
        margin-top: 3rem;
    }
    &_list{
        /* NEED TO INLINE BULLET POINTS! */
        list-style-type: none;
        list-style-position:inside;
        margin-bottom: 1rem;
/*         
        li{ 
            display: inline;
            vertical-align: middle;
        } */
           }



    &_list-title{
        margin-bottom: 1rem;
        font-family: ${props => props.theme.MPLight};
        font-size: 2rem;
    }
    &_sub-title{
        color: ${props => props.theme.primary};
        font-size: 3.5rem;
        font-family: ${props => props.theme.MPLightIt};
        padding-bottom: .5rem;
    }
    &-bold{
        font-family: ${props => props.theme.MPBold};
    }
    &-italic{
        font-family: ${props => props.theme.MPLightIt}
    }
    &-boldIt{
        font-family: ${props => props.theme.MPBoldIt}
    }

}
    .button_box{
        display: flex;
        justify-content: flex-start;
    }
    .infoBox{
        display: flex;
        flex-flow: column;
        justify-content: center;
        min-height: 24rem;
        background-color: ${props => props.theme.primary2};
        width: 100vw;
        position: relative;
        left: 50%;
        right: 50%;
        margin-left: -50.5vw;
        margin-right: -50.5vw;
        /* dont know why but it gves margin on 50%vh */

        &-text{
        padding: 2rem;
        align-self: center;
        width: 50%;
             @media only screen 
  and (max-device-width: 768px) 
  and (-webkit-min-device-pixel-ratio: 2) { 
       width: 80%;
    };   
        } 
    }

    .video-box{
        /* display: flex; */

  position: relative;
  padding-top: 56.25%  /*Player ratio: 100 / (1280 / 720) */
}

.react-player {
  position: absolute;
  top: 0;
  left: 0;
}


    .margin-top5{
        margin-top: 5rem;

    }
    .margin-bottom1.5{
        margin-bottom: 1.5rem;
    }
`;

const StyledQuote = styled.div`
    margin-top: 5rem; 

     /* position: relative; */
/*
    .Quote-text_box{

        padding: 2.5rem;
        position: absolute;
        display: flex;
        flex-flow: column;
        justify-content: space-between;
        height: 100%;
        width: 90%;
    }
    .Quote-text{
        flex: 1 1 auto;
        color: white;
        font-size: 2rem;
        font-family: ${props => props.theme.MPLight};

        &_bold{
        font-family: ${props => props.theme.MPBold};
    }
    } */
`;

// ! NEED TO SORT OUT MEDIA QUERIS FOR TEXT DUE TO SVG NOT SCALING WITH FONT
//  * OPTIONS ARE TO REDO QOUTE MARKS AND SET THE Z-INDEZX AS -100? 

export default () => (
    <>
        <HeroSection 
        t="30rem" 
        max="55%"
        src="/images/BarClip.png"
             >
            Student Opportunities Programme

        </HeroSection>
        <StyledPage>
            <div className="Esop">

            <Typography gutterBottom className="Esop-title ">Student Opportunities Programme</Typography>
            <div className="video-box margin-bottom1.5 margin-top5">
            <ReactPlayer className="react-player" url='https://www.youtube.com/embed/N-Nq9BH9-W4' playing width="100%" height="100%" />
            {/* <iframe width="480" height="270" src="https://www.youtube.com/embed/N-Nq9BH9-W4" frameborder="0" allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture" allowfullscreen></iframe> */}
            </div>
            <Typography gutterBottom className="Esop Esop-bold margin-top5">About the programme</Typography>
            <Typography gutterBottom className="Esop ">The EuroBrake Student Opportunities Programme (ESOP) offers students from around the world the chance to attend EuroBrake 2020.</Typography>
            <Typography gutterBottom className="Esop ">Selected students will be offered the opportunity to attend EuroBrake via a sponsorship package which offers a strong networking opportunities, a CV check/one to one career advice with HR representatives from the sponsor companies, access to the designated Student Lounge and much more. </Typography>
            <Typography gutterBottom className="Esop ">The 2020 application round is now open. </Typography>
            <div className="button_box">

            <Button
            background="#134381"
            br="100rem"
            bcolor="#134381"
            style={{margin: '3.5rem 0', }}
            >Apply here</Button>
            </div>

            <StyledQuote>
                <Quote2/>
            </StyledQuote>

            <Typography gutterBottom className="Esop-title"></Typography>

            <div className="">
            <Typography gutterBottom className="Esop  Esop-bold" style={{}}>For more information please contact FISITA: </Typography>
            <Typography gutterBottom className="Esop  Esop-bold" style={{}}><PhoneIcon width="20" height="20" style={{marginRight: '1rem'}}/> +44 (0) 1279883470 </Typography>
            <Typography gutterBottom className="Esop Esop-bold" style={{}}><MailIcon width="20" height="20" style={{marginRight: '1rem'}}/> sales@fisita.com</Typography>
            </div>
             <div className="button_box">

             <Button
            background="#134381"
            br="100rem"
            bcolor="#134381"
            style={{margin: '3.5rem 0'}}
            >DOWNLOAD THE sPONSORSHIP BROUCHURE HERE</Button>
            </div>
             </div>
        </StyledPage>
    </>
)





