import React, { Component } from 'react'
import styled from 'styled-components';
import makeCarousel from 'react-reveal/makeCarousel';
import Slide from 'react-reveal/Slide';


const Container = styled.div`
display: flex;
justify-content: center;
align-items: center;
position: relative;
overflow: hidden;
flex: 2 1 80%;

`;

const StyledSlide = styled.div` 
flex: 1;
background-image: url("/images/pic11.png");
width: 100%;
height: 100%;
/* flex: 1 1 auto;  */
 img{
object-fit: fill;
object-position: cover;
width: 100%;
height: 100%;

  }
`;



const CarouselUI = ({ children }) => <Container>{children}</Container>;
const Carousel = makeCarousel(CarouselUI);

export default function StyledCarousel() {
    return (

        <Carousel defaultWait={4000} /*wait for 1000 milliseconds*/>
            <Slide right style={{}}>
                <StyledSlide>

                    {/* <img srcSet="/images/pic11.png" alt="EuroBrake Greating"/> */}
                </StyledSlide>
            </Slide>
            <Slide right>
                <StyledSlide>

                    {/* <img srcSet="/images/pic2.png" alt="EuroBrake Greating"/> */}
                </StyledSlide>
            </Slide>
        </Carousel>


    )
}
