import React, { Component, useState } from 'react'
import { Formik, Form, Field, } from 'formik';
import { TextField} from 'formik-material-ui';
import CircularProgress from '@material-ui/core/CircularProgress';
import { StyledForm } from './Formstyles';
import { Button } from '../Button';
import { SendForm } from './FormActions';
import {LoginSchema} from './FormControl';




export const Newsletter = () => {
    const [Loading, setLoading] = useState(false);
    const [Toggle, setToggle] = useState(false);
    const [Status, setStatus] = useState(null);
    const FT = "newsletter"
    const url = 'https://prelude.eurobrake.net/signup';
    console.log({url})
    return (
        <Formik
            initialValues={emptyInitial}
            enableReinitialize
        >
            {({ values, handleChange, validateForm }) => {

                const onSubmit = async () => {
                   setLoading(true);
                  {/* values.__csrf_token = csrf */}
                    console.log('submitting')
                  let res =  await SendForm({values, url, FT});
                  let data = await  res && res.status;
                  console.log({status})
                 data && setStatus(data) && setToggle(true) && setLoading(false)
                  return 
              }

                return (
                    <StyledForm>
                      <div className="form" >

                        <div className="form-field">
                            <label
                                htmlFor="name"
                                className="">
                                Name:
                        </label>
                            <Field
                                type="text"
                                className="form-input"
                                onClick={handleChange}
                                value={values.name}
                                style={{ }}
                                name="name"
                                component={TextField}
                            />
                        </div>
                        <div className="form-field">
                            <label
                                htmlFor="email"
                                className="">
                                Email:
                        </label>
                            <Field
                                type="email"
                                className="form-input"
                                onClick={handleChange}
                                value={values.name}
                                style={{  }}
                                name="email"
                                component={TextField}
                            />
                        </div>
                       {Loading ?  <CircularProgress size={24} className="loading"/> : 
                       <Button 
                        onClick={onSubmit}
                           padding=".5rem " 
                           style={{ fontSize: "1rem", height: '2rem', alignSelf: 'center'}}>
                            {Status ? Status : 'submit'}
                        </Button>}
                      </div>
                    </StyledForm>
                )
            }}
        </Formik>
    )
};
