            if (data.status === 'success') {
                console.log('getForm', data);
                return data
            } else {
                let error = new Error(data.error)
                error.response = response
                throw error
            }
        } catch (error) {
            console.error(
                'Failed to get form, please try again', error
            )
        }
    }
    if(url === 'https://prelude.eurobrake.net/authors/login' ){
