            if (data.status === 'success') {
            console.log('getForm', data);
            return data

            } else {
                let error = new Error(data.error)
                error.response = response
                throw error
            }
        } catch (error) {
            console.error(
                'Failed to reset, please try again', error
            )
         }
        }
    if(FT === 'newsletter' || FT === 'contact' && csrf ){
