import React, { Component } from 'react';
import styled from 'styled-components';
import { Button } from '../components/Button';


const CDBanner = styled.div`
    background-color: #134381;
    margin-top: 0;
    min-width: 100%;
    height: 19.5rem;
    display: flex;
    justify-content: center;
    align-items: center;
    color: white; 
`;

const CDContainer = styled.div`
    display: flex;
    max-width: ${props => props.theme.maxWidth};
    align-items: flex-start;
    margin-right: 2rem; 
`;

const CDItem = styled.div`
    position: relative;
    display: flex;
    flex-flow: column;
    align-items: flex-start;
    font-size: 7rem;
    font-family: ${props => props.theme.MPBlack};
    margin: 0 1rem ;

    span{
    position: absolute;
    top: 9rem;
    font-size: 1.7rem;
    font-family: ${props => props.theme.MPLight};
    padding-left: 1rem;
    }

    span:not(.noPad){
        padding-left: 4rem;
    }
`;

class CountDownBanner extends Component {
    constructor(props) {
        super(props);

        this.state = {
            days: undefined,
            hours: undefined,
            minutes: undefined,
            seconds: undefined,

        }
    }

    componentDidMount() {

        this.interval = setInterval(() => {
            const countDownDate = new Date("Jun 2, 2020 08:00:00").getTime();
            const now = new Date().getTime();
            const distance = countDownDate - now;
            const days = Math.floor(distance / (1000 * 60 * 60 * 24));
            const hours = Math.floor((distance / (1000 * 60 * 60)) % 24);
            const minutes = Math.floor((distance / (1000 * 60)) % 60);
            const seconds = Math.floor((distance / 1000) % 60);
            this.setState({ days, hours, minutes, seconds });
        }, 1000);

    }

    componentWillUnmount() {
        clearInterval(this.interval);
    }

    render() {
        const { days, hours, minutes, seconds } = this.state

        return (
            <CDBanner>
                <CDContainer>
                    <CDItem>
                        {days}
                        <span className="noPad">Days</span>
                    </CDItem>
                    <CDItem className="countdown-item">
                        : {hours > 9 ? hours : `0` + hours}
                        <span>Hours</span>
                    </CDItem>
                    <CDItem className="countdown-item">
                        : {minutes > 9 ? minutes : `0` + minutes}
                        <span>Minutes</span>
                    </CDItem>
                    <CDItem className="countdown-item">
                        : {seconds > 9 ? seconds : `0` + seconds}
                        <span>Seconds</span>
                    </CDItem>
                    <Button style={{marginLeft: '5rem'}}>Book Now</Button>
                </CDContainer>
            </CDBanner>

        )
    }
};

export default CountDownBanner;



