import styled from 'styled-components';
import {device} from '../Page';


export const StyledPage = styled.div`
    display: flex;
    flex-flow: column;
    align-self: center;
    margin-top: ${props => props.mt || '5rem'};
    @media ${device.mobile} and (-webkit-min-device-pixel-ratio: 2) {
    width: ${props => props.max || props.theme.mobileWidth}
    }

    @media ${device.mobile} {
    width: ${props => props.max || props.theme.mobileWidth}
    }
    @media ${device.tablet} {
    width: ${props => props.max || props.theme.tabletWidth}
    }
    @media ${device.laptopM} {
    width: ${props => props.max || props.theme.minWidth};
    }
    background-color: ${props => props.theme.white};
    margin-top: 7rem;

    h1{
        font-family: ${props => props.theme.MPBlack};
        color: ${props => props.theme.primary};
        font-size: 4.4rem;
    }
`;

export const IframeContent = styled.iframe`
    align-self: center;
    margin-top: 2rem;
    min-width: 100rem;
    min-height: 100rem;
    border: none;
    background-color: transparent;
    /* overflow: hidden; */
    text-decoration: none;
`;


export const StyledBanner = styled.div`
    background-color: #134381;
    min-width: 100%;
    display: flex;
    justify-content: center;
    align-items: center;
    color: white; 
`;

export const StyledContainer = styled.div`
    display: flex;
    flex-flow: column;
    min-width: ${props => props.theme.minWidth};
    padding: 3rem 0;

    .banner-heading{
        font-family: ${props => props.theme.MPBlack};
        font-size: ${props => props.hfs || '4.4rem'};
        padding-bottom: .5rem;
        margin-bottom: .5rem;
        border-bottom: 2px solid ${props => props.theme.white};
    }

    .banner-sub{
        font-family: ${props => props.theme.MPLightIt};
        font-size: ${props => props.sfs || '3.8rem'};
        font-weight: 50;

    }

    .banner_bottom{
        display: flex;
        align-self: flex-end;
    &-icon{
        color: ${props => props.theme.primary};
        font-size: 1.5rem;
        background-color: white;
        border: 1px solid white;
        border-radius: 50%;
        margin: .5rem;
    } 
    &-details{
        font-family: ${props => props.theme.MPSemibold};
        font-size: 1.6rem;
    }
}
`;
