import * as yup from 'yup';
export var PaperSchema = yup.object().shape({
  accept: yup.bool().test('accept', 'Field must be checked', function (value) {
    return value === true;
  }).required(),
  technicalpaper_filename: yup.mixed().test("fileFormat", "Unsupported Format", function (value) {
    return value && SUPPORTED_FORMATS.includes(value.type);
  }).required('Required'),
  author_notes: yup.string(),
  copyright: yup.bool().test('copyright', 'Field must be checked', function (value) {
    return value === true;
  }).required()
});
var SUPPORTED_FORMATS = ['application/pdf'];
