import React from 'react'
import styled from 'styled-components';
import Link from 'next/link';
import HeroSection from '../components/HeroSection';

const StyledPage = styled.div`
    display: flex;
    flex-flow: column;
    align-self: center;
    margin-top: 5rem;
    max-width: ${props => props.theme.maxWidth};
    background-color: ${props => props.theme.white};

    h1{
        font-family: ${props => props.theme.MPBlack};
        color: ${props => props.theme.primary};
        font-size: 4.4rem;
    }
`;


const StyledBanner = styled.div`
    background-color: #134381;
    min-width: 100%;
    display: flex;
    justify-content: center;
    align-items: center;
    color: white; 
`;

const StyledContainer = styled.div`
    display: flex;
    flex-flow: column;
    width: ${props => props.theme.maxWidth};
    padding: 3rem 0;

    .banner-heading{
        font-family: ${props => props.theme.MPBlack};
        font-size: 4.4rem;
        padding-bottom: .5rem;
        margin-bottom: .5rem;
        border-bottom: 2px solid ${props => props.theme.white};
    }

    .banner-sub{
        font-family: ${props => props.theme.MPLightIt};
        font-size: 3.8rem;
        font-weight: 50;

    }

    .banner_bottom{
        display: flex;
        align-self: flex-end;
    &-icon{
        color: ${props => props.theme.primary};
        font-size: 1.5rem;
        background-color: white;
        border: 1px solid white;
        border-radius: 50%;
        margin: .5rem;
    } 
    &-details{
        font-family: ${props => props.theme.MPSemibold};
        font-size: 1.6rem;
    }
}
`;

const FilledBanner = () => (

    <StyledBanner>
        <StyledContainer>

            <Typography className="banner-heading" >Book your tours and accomodation here</Typography>
            <Typography className="banner-sub" >Find out about Barcelona</Typography>
            <div className="banner_bottom">
                <ExpandMoreOutlinedIcon className="banner_bottom-icon" />
                <Typography varient="h3" className="banner_bottom-details">See below for more details</Typography>
            </div>
        </StyledContainer>
    </StyledBanner>
)

export default () => (
<>
    <HeroSection t="15rem"  banner={FilledBanner} fs="8.9rem">
        Welcome to Barcelona section
    </HeroSection>
    <StyledPage>
<h1> In here there will be :
        General information
        Information about barcelona
        Acomodation 
        and tours</h1>
    </StyledPage>
</>
);
