import React, { Component, useState, useEffect } from 'react'
import { Formik, Form, Field } from 'formik';
import { TextField, Select} from 'formik-material-ui';
import { Button } from '../Button';
import {titles, countries} from './FormSelects';
import {AbstractSchema} from './FormControl';
import {StyledForm} from './Formstyles';
import { GetForm } from './FormActions';


export const Authorprofile = ({editPaper = false}) => {
    useEffect(() => {
       console.log({editPaper})

    }, [editPaper])

    return (
        <Formik
            initialValues={ emptyInitial }
            onSubmit={(values, actions) => {
                setTimeout(() => {
                    alert(JSON.stringify(values, null, 2));
                    actions.setSubmitting(false);
                }, 1000)
            }}
            enableReinitialize
        >
            {({ values, handleChange}) => {

                return (
                    <StyledForm>
                        <div className="form-field">
                            <label
                                htmlFor="label"
                                className="form-label">
                                Title
                        </label>
                            <Field
                                className="form-input"
                                onClick={handleChange}
                                value={values.author_title}
                                style={{ color: '#134381' }}
                                name="author_title"
                                component={Select}
                            >
                                {titles.map((option, i) =>
                                    <option key={i} style={{ fontSize: '1.5rem', cursor: 'pointer' }} value={option.value}>{option.label}</option>)}
                            </Field>
                        </div>
                        <div className="form-field">
                            <label
                                htmlFor="label"
                                className="form-label">
                                First Name:
                        </label>
                            <Field
                                className="form-input"
                                onClick={handleChange}
                                value={values.author_firstname}
                                style={{ color: '#134381' }}
                                name="author_firstname"
                                component={TextField}
                            />
                        </div>
                        <div className="form-field">
                            <label
                                htmlFor="label"
                                className="form-label">
                                Last Name:
                        </label>
                            <Field
                                className="form-input"
                                onClick={handleChange}
                                value={values.author_lastname}
                                style={{ color: '#134381' }}
                                name="author_lastname"
                                component={TextField}
                            />
                        </div>
                        <div className="form-field">
                            <label
                                htmlFor="label"
                                className="form-label">
                                Email:
                        </label>
                            <Field
                                className="form-input"
                                onClick={handleChange}
                                value={values.author_email}
                                style={{ color: '#134381' }}
                                name="author_email"
                                component={TextField}
                            />
                        </div>
                        <div className="form-field">
                            <label
                                htmlFor="label"
                                className="form-label">
                                Job Title:
                        </label>
                            <Field
                                className="form-input"
                                onClick={handleChange}
                                value={values.author_jobtitle}
                                style={{ color: '#134381' }}
                                name="author_jobtitle"
                                component={TextField}
                            />
                        </div>
                        <div className="form-field">
                            <label
                                htmlFor="label"
                                className="form-label">
                                Company:
                        </label>
                            <Field
                                className="form-input"
                                onClick={handleChange}
                                value={values.author_company}
                                style={{ color: '#134381' }}
                                name="author_company"
                                component={TextField}
                            />
                        </div>
                        <div className="form-field">
                            <label
                                htmlFor="label"
                                className="form-label">
                                Address:
                        </label>
                            <Field
                                className="form-input"
                                onClick={handleChange}
                                value={values.author_address1}
                                style={{ color: '#134381' }}
                                name="author_address1"
                                component={TextField}
                            />
                        </div>
                        <div className="form-field">
                            <label
                                htmlFor="label"
                                className="form-label">

                            </label>
                            <Field
                                className="form-input"
                                onClick={handleChange}
                                value={values.author_address2}
                                style={{ color: '#134381' }}
                                name="author_address2"
                                component={TextField}
                            />
                        </div>
                        <div className="form-field">
                            <label
                                htmlFor="label"
                                className="form-label">

                            </label>
                            <Field
                                className="form-input"
                                onClick={handleChange}
                                value={values.author_address3}
                                style={{ color: '#134381' }}
                                name="author_address3"
                                component={TextField}
                            />
                        </div>
                        <div className="form-field">
                            <label
                                htmlFor="label"
                                className="form-label">
                                City:
                        </label>
                            <Field
                                className="form-input"
                                onClick={handleChange}
                                value={values.author_city}
                                style={{ color: '#134381' }}
                                name="author_city"
                                component={TextField}
                            />
                        </div>
                        <div className="form-field">
                            <label
                                htmlFor="label"
                                className="form-label">
                                State/ County/ Province:
                        </label>
                            <Field
                                className="form-input"
                                onClick={handleChange}
                                value={values.author_scp}
                                style={{ color: '#134381' }}
                                name="author_scp"
                                component={TextField}
                            />
                        </div>
                        <div className="form-field">
                            <label
                                htmlFor="label"
                                className="form-label">
                                Postal/Zip code:
                        </label>
                            <Field
                                className="form-input"
                                onClick={handleChange}
                                value={values.author_postal}
                                style={{ color: '#134381' }}
                                name="author_postal"
                                component={TextField}
                            />
                        </div>
                        <div className="form-field">
                            <label
                                htmlFor="label"
                                className="form-label">
                                Country
                        </label>
                            <Field
                                className="form-input"
                                onClick={handleChange}
                                value={values.author_country}
                                style={{ color: '#134381' }}
                                name="author_country"
                                component={Select}
                            >
                                {countries.map((option, i) =>
                                    <option key={i} style={{ fontSize: '1.5rem', cursor: 'pointer' }} value={option.value}>{option.label}</option>)}
                            </Field>
                        </div>
                        <div className="form-field">
                            <label
                                htmlFor="label"
                                className="form-label">
                                Phone:
                        </label>
                            <Field
                                className="form-input"
                                onClick={handleChange}
                                value={values.author_phone}
                                style={{ color: '#134381' }}
                                name="author_phone"
                                component={TextField}
                            />
                        </div>
                        <div className="form-field">
                            <label
                                htmlFor="label"
                                className="form-label">
                                Fax:
                        </label>
                            <Field
                                className="form-input"
                                onClick={handleChange}
                                value={values.author_fax}
                                style={{ color: '#134381' }}
                                name="author_fax"
                                component={TextField}
                            />
                        </div>
                        <div className="form-field">
                            <label
                                htmlFor="label"
                                className="form-label">
                                Choose a Password:
                        </label>
                            <Field
                                className="form-input"
                                onClick={handleChange}
                                value={values.author_phone}
                                style={{ color: '#134381' }}
                                name="author_password"
                                component={TextField}
                            />
                        </div>
                        <div className="form-field">
                            <label
                                htmlFor="label"
                                className="form-label">
                                Confirm password:
                        </label>
                            <Field
                                className="form-input"
                                onClick={handleChange}
                                value={values.author_phone}
                                style={{ color: '#134381' }}
                                name="author_password_confirm"
                                component={TextField}
                            />
                        </div>


                        <Button type="submit" 
                         bcolor="#134381"
                                background="#134381"
                                br="100rem"
                                style={{ marginTop: "1rem" }}
                                fontSize="1.7rem">Submit</Button>
                        {console.log('main', values.is_presenting_author, 'second', values.secondary_authors)}
                    </StyledForm>
                )
            }}
        </Formik>
    )
};

// const presets = {
//     author_title: "Mr.",
//     author_firstname: "Andrew",
