import React, { Component } from 'react';
import styled from 'styled-components';
import {Button} from '../components/Button';


const CDBanner = styled.div`
    background-color: #134381;
    margin-top: 0;
    min-width: 100%;
    height: 19.5rem;
    display: flex;
    justify-content: center;
    align-items: center;
    color: white; 
`;

const CDContainer = styled.div`
    display: flex;
    max-width: 50%;
    margin-right: 2rem; 
`;

const CDItem = styled.div`
    display: flex;
    flex-flow: column;
    font-size: 7rem;
    line-height: 1rem;
    font-family: ${props => props.theme.MPBlack};
    margin: 0 1rem ;

    span{
        margin-left: 1.5rem;
    font-size: 1.7rem;
    font-family: ${props => props.theme.MPLight};
    }
`;

 class CountDownBanner extends Component {
    constructor(props) {
        super(props);

        this.state ={
           days: undefined,
           hours: undefined,
           minutes: undefined,
           seconds: undefined,

        }
    }

   componentDidMount(){

    this.interval = setInterval(() => {
     const countDownDate = new Date("Jun 2, 2020 08:00:00").getTime();
     const now = new Date().getTime();
     const distance = countDownDate - now;
     const days = Math.floor(distance / (1000 * 60 * 60 * 24));
     const hours = Math.floor((distance / (1000 * 60 * 60)) % 24); 
     const minutes =  Math.floor((distance / (1000 * 60 )) % 60);
     const seconds =  Math.floor((distance / 1000) % 60 );
        this.setState({ days, hours, minutes, seconds });
    }, 1000);

    }

    componentWillUnmount(){
    clearInterval(this.interval);
    }

    render()
    {
     const {days, hours, minutes, seconds} = this.state

        return (
        <CDBanner>
             <CDContainer>
                    <CDItem>
                        {days} :
                        <span>days</span>
                    </CDItem>
                   <CDItem className="countdown-item">
                        {hours < 10 ? hours : 0,hours} :
                        <span>Hours</span>
                    </CDItem>
                    <CDItem className="countdown-item">
                        {minutes < 10 ? minutes : 0,minutes} :
                        <span>Minutes</span>
                    </CDItem>
                    <CDItem className="countdown-item">
                        {seconds < 10 ? seconds : 0,seconds}
                        <span>Seconds</span>
                    </CDItem>
                </CDContainer>
            <Button>Book Now</Button>
        </CDBanner>

        )
    }
 };

export default CountDownBanner;
