import React, { Component } from 'react'
import Router from 'next/router';
import cookies from 'next-cookies';
import cookie from 'js-cookie';
import { Link } from '@material-ui/core';
import fetch from 'isomorphic-unfetch';
import styled from 'styled-components';
import Typography from '@material-ui/core/Typography';
import ExpandMoreOutlinedIcon from '@material-ui/icons/ExpandMoreOutlined';
import CreateIcon from '@material-ui/icons/Create';
import AssignmentIcon from '@material-ui/icons/Assignment';
import ExitToAppIcon from '@material-ui/icons/ExitToApp';
import CheckCircleIcon from '@material-ui/icons/CheckCircle';
import CancelIcon from '@material-ui/icons/Cancel';
import { withAuthSync, logout, login } from '../utils/auth'
import HeroSection from '../components/HeroSection';
import { Button } from '../components/Button';
import { StyledBanner, StyledContainer } from '../components/styles/PageStyles';
import { Abstracts } from '../components/testAuthors';


const StyledPage = styled.div`
    display: flex;

    flex-direction: column;
    align-self: center;
    width: ${props => props.theme.minWidth};
    background-color: ${props => props.theme.white};
    margin-top: 7rem;
    .authors{


        color: ${props => props.theme.primary};
        font-family: ${props => props.theme.MPLight};
        font-size: 2rem;
    &-title{
        color: ${props => props.theme.primary};
        font-family: ${props => props.theme.MPSemibold};
        font-size: 4.4rem;

    }
    &_sub-title{
        color: ${props => props.theme.primary};
        font-size: 3.5rem;
        font-family: ${props => props.theme.MPLightIt};
        padding-bottom: .5rem;
    }
    &-bold{
        color: ${props => props.theme.primary};
        font-family: ${props => props.theme.MPBold};
    }
    &-it{
        font-family: ${props => props.theme.MPLightIt};
    }
    &_action-box{
        display: flex;
        justify-content: space-between;
        margin-bottom: 3rem;
    }
}
`;

const StyledBox = styled.div`
    display: flex;
    justify-content: space-between;
    min-height: 15rem;
    background-color: #D4DDE8;
    padding: 1rem;

`;

const StyledActionArea = styled.div`
    flex: 1 1 33.33%;
    display: flex;
    flex-flow: column;
    justify-content: space-between;
    align-items: flex-start;

    .action-paper{
        font-family: ${props => props.theme.MPBold};
        font-size: 2rem;
    }
`;

const StyledInfoArea = styled.div`
    flex: 1 1 66.66%;
    display: flex;
    flex-flow: column;
    justify-content: space-between;
    align-items: flex-start;
`;


const FilledBanner = () => (

    <StyledBanner>
        <StyledContainer >
            <Typography className="banner-heading"  >Welcome to the Author's Area</Typography>
            <Typography className="banner-sub" sfs="3rem">Submit your review ready papers by 26 January 2020</Typography>
            <div className="banner_bottom">
                <ExpandMoreOutlinedIcon className="banner_bottom-icon" />
                <Typography varient="h3" className="banner_bottom-details">Submit your review ready papers by 26 January 2020</Typography>
            </div>
        </StyledContainer>
    </StyledBanner>
)


const Abstract = () => {

    <StyledBox>
        <StyledActionArea>
            <Typography gutterBottom className="action-paper">{Abstracts[0].papercode}</Typography>
            <Button
                bcolor="#134381"
                background="#134381"
                br="100rem"
                padding="0.5rem 2rem"
                style={{ margin: ".5rem 0" }}
                fontSize="1.7rem"
            >
                <CreateIcon style={{ fontSize: '3rem', marginRight: '1rem' }} />
                EDIT PROFILE
            </Button>
            <Button
                bcolor="#134381"
                background="#134381"
                br="100rem"
                padding="0.5rem 2rem"
                style={{ margin: ".5rem 0" }}
                fontSize="1.7rem"
            >
                <CancelIcon style={{ fontSize: '3rem', marginRight: '1rem' }} />
                WITHDRAW
            </Button>
        </StyledActionArea>
        <StyledInfoArea>
            <Typography gutterBottom className="paper-title">{Abstracts[0].title}</Typography>
            <Typography gutterBottom className="paper-type">{Abstracts[0].type}</Typography>
            <ul classname="paper_tasks">
                <li className="paper_tasks-task">{Abstracts[0].tasks && Abstracts[0].tasks.map(task => (
                    <>
                    <Typography gutterBottom className="paper-task">{task.task}</Typography>
                    {task.done === null ? <CheckCircleIcon/> : <CancelIcon/>}
                    </>
                ))}</li>
            </ul>
        </StyledInfoArea>
    </StyledBox>

}


class AuthorsAreaDash extends Component {
    constructor(props) {
        super(props);
        this.state = {
            hasForm: false,
            formData: undefined,
            error: '',
            userLoggedIn: false,
        }


    }

    componentDidMount = () => {
        const { userLoggedIn } = this.state;
        const { logintoken } = cookie.get();
        console.log('cdm', logintoken)
        logintoken && !userLoggedIn ? this.setState(prev => ({
            userLoggedIn: !prev
        })) : null
    }

    getAbstractForm = async (e) => {
        e.preventDefault()
        console.log('click');
        const { logintoken } = cookie.get();
        console.log(logintoken)
        try {
            const apiUrl = 'https://prelude.eurobrake.net/submit';
            const response = await fetch(apiUrl, {
                credentials: 'include',
                headers: {
                    Authorization: `Bearer ${logintoken}`,
                }
            });
            const data = await response.json();
            if (data.status === 'success') {
                this.setState(prev => (
                    {
                        hasForm: !prev,
                        formData: data.form
                    }));
                // console.log(data.form);
                // console.log('stringify',JSON.stringify(data.form));
                console.log(data)


            } else {
                let error = new Error(data.error)
                error.response = response
                throw error
            }
        } catch (error) {
            console.error(
                'Failed to get form, please try again', error
            )
            this.setState({ error: error.message });
        }
    }


    handleInputChange = (event) => {
        const target = event.target;
        const value = target.type === 'checkbox' ? target.checked : target.value;
        const name = target.name;

        this.setState({
            [name]: value
        });
    }

    render() {
        // const {name} = this.props.authorData;
        const { userLoggedIn, hasForm } = this.state;
        console.log({ userLoggedIn, hasForm });
        return (
            <>
                <HeroSection banner={FilledBanner}>
                    Author's Area Dashboard
            </HeroSection>
                <StyledPage>
                    <div className="authors">

                        <Typography className="authors-title">Hi {'*USERNAME*'}</Typography>
                        <Typography gutterBottom className="authors_sub-title">Welcome to the Author's Area</Typography>
                        <div className="authors_action-box">
                            <Button
                                bcolor="#134381"
                                background="#134381"
                                br="100rem"
                                padding="0.5rem 2rem"
                                style={{ margin: ".5rem 0" }}
                                fontSize="1.7rem"
                            >
                                <CreateIcon style={{ fontSize: '3rem', marginRight: '1rem' }} />
                                EDIT PROFILE
                    </Button>
                            <Link href="/authorsAreaInstructions">
                                <Button
                                    bcolor="#134381"
                                    padding="0.5rem 2rem"
                                    background="#134381"
                                    br="100rem"
                                    style={{ margin: ".5rem" }}
                                    fontSize="1.7rem"
                                >
                                    <AssignmentIcon style={{ fontSize: '3rem', marginRight: '.5rem' }} />
                                    INSTRUCTIONS & TEMPLATES
                    </Button>
                            </Link>
                            <Button
                                bcolor="#134381"
                                padding="0.5rem 2rem"
                                background="#134381"
                                br="100rem"
                                style={{ margin: ".5rem 0" }}
                                fontSize="1.7rem"
                                onClick={logout}>
                                <ExitToAppIcon style={{ fontSize: '3rem', marginRight: '.5rem' }} />
                                logout
                    </Button>
                        </div>
                        <Typography gutterBottom className="authors-title" style={{ fontSize: '3rem', borderTop: '2px solid #134381', borderBottom: '2px solid #134381' }}>Your Abstracts</Typography>
                        { Abstracts ? 
                        <Abstract/> :
                        <StyledBox>
                            <Typography gutterBottom className="authors-it" style={{ fontSize: '2rem' }}>You haven't submitted any abstracts yet</Typography>
                        </StyledBox>
                        }
