import React, { Component } from 'react'
import styled from 'styled-components';
import Typography from '@material-ui/core/Typography';
import ExpandMoreOutlinedIcon from '@material-ui/icons/ExpandMoreOutlined';
import HeroSection from '../../components/HeroSection';
import { StyledBanner, StyledContainer } from '../../components/styles/PageStyles';
import InstructionsPopup from '../../components/InstructionsPopup';
import {Button } from '../../components/Button';
import Link from 'next/link';
import {FixedDates, FixedBox} from '../../components/Dates';



const FilledBanner = () => (

    <StyledBanner>
        <StyledContainer >
            <Typography className="banner-heading"  >Submit your review ready papers</Typography>
            <Typography className="banner-sub" >Manage your submission in the Author's Area Dashboard</Typography>
            {/* <div className="banner_bottom">
                <ExpandMoreOutlinedIcon className="banner_bottom-icon" />
                <Typography varient="h3" className="banner_bottom-details"></Typography>
            </div> */}
        </StyledContainer>
    </StyledBanner>
)


const StyledPage = styled.div`
    display: flex;
