import React, { Component, Fragment } from 'react'
import fetch from 'isomorphic-unfetch';
import styled from 'styled-components';
import ExhibitorCard from '../components/exhibitors/ExhibitorCard';
import {expo} from '../components/exhibitors/testexpo';
import HeroSection from '../components/HeroSection';

const StyledPage = styled.div`
    display: flex;
    flex-direction: column;
    align-self: center;
    margin-top: 10rem;
    max-width: ${props => props.theme.maxWidth};
    background-color: ${props => props.theme.white};

    h1{
        font-family: ${props => props.theme.MPBlack};
        color: ${props => props.theme.primary};
        font-size: 4.4rem;
    }
`;


const StyledExhibitors = styled.div`
    display: flex;
    flex-flow: wrap; 
    justify-content: center;
`;



export default class ExhibitorList extends Component {
    constructor(props) {
        super(props)

        this.state = {
            exhibitors: [],
            companies: [],
        }
    }

    // static async getInitialProps() {
    //     const res = await fetch('https://prelude.eurobrake.net/exhibitors');
    //     const data = await res.json().catch(error => console.log(error));
    //     return {
    //         exhibitors: data
    //     };
    // };

    componentDidMount() {

        const exhibitors = expo;
        // this.props.exhibitors.exhibitors;
        const company = exhibitors.map((ex, i) => ex.company);
        this.setState({ companies: company });
    }

    render() {
        const exhibitors = expo;

        // this.props.exhibitors.exhibitors;
        return (
            <Fragment>
            <HeroSection>
                EuroBrake 2020 Exhibitor Directory
            </HeroSection>
            <StyledPage>
                <h1>Get to know the exhibitors</h1>
                <StyledExhibitors>
                    {exhibitors.map((company, i) => {
                        return (
                            <ExhibitorCard 
                            key={i} 
                            name={company.company} 
                            image={company.logo}
                            booth={company.booth}
                            country={company.country}
                            height={100}
                            summary={company.description}
                            website={company.website}
                            phone={company.phone}
                            email={company.email}
                            />
                        )
                    })}
                </StyledExhibitors>
            </StyledPage>
            </Fragment>
        )
    }
}

// ON THIS PAGE!!!
// HAVE A BIG CARD LIST OF ALL EXHIBITORS
// DISPLAY LOGO NAME AND BOOTH
// THEN ON CLICK SHOW POPUP PORTAL WITH THE REST OPF THEIR DETAILS
