import styled, { keyframes } from 'styled-components';
import BaseAnimation from './BaseAnimation';

export const NavStyles = styled.div`

    display: flex;
    position: absolute;
    transform: translateX(-20rem);
    z-index: 200;
    a{  
        font-family: ${props => props.theme.MPBold};
        font-size: 2.6rem;
        color: ${props =>  props.theme.secondary};
        margin: 1rem;
        text-transform: uppercase;
        cursor: pointer;
        z-index: 100;

           @media only screen 
        and (max-device-width: 1200px) 
        and (-webkit-min-device-pixel-ratio: 2) { 
    font-size: 2.3rem;               
}
    }
`;
const Rotate360 = keyframes`

    100%{
        transform: rotate(360deg);
    }
`;

export const Rotate = styled(BaseAnimation)`
    animation-name: ${Rotate360};
    transform-origin: center center;
`;


export const Burger = styled.div`
    position: absolute;
    top:  -4rem;
    left: 5rem;
    margin: 0 1rem;
    width: 6rem;
    height: 8rem; 
    cursor: pointer; 
    display: flex;
    align-items: center;
    z-index: 300;
    transition: all .6s linear;
    /* background-color: salmon; */

`;

const trans = {
    up: 'translateY(1.2rem)',
    stay: 'translateY(0)',
    down: 'translateY(-1.2rem)',
    spin0: 'rotate(0)',
    spin90: 'rotate(90deg)',
    spin315: 'rotate(315deg)',
    scale100: 'scale(107)',
    scale0: 'scale(0)',
    in: 'transform .6s cubic-bezier(0.22, 0.61, 0.36, 1) .3s',
    out: 'transform .6s cubic-bezier(.55,.055,.675,.19); .3 '
}

export const TopBun = styled.div`
    position: relative;
    width: 100%;
    height: .3rem;
    transition: ${props => props.isOpen ?
        trans.in : trans.out}; 
    background-color: ${props => props.theme.secondary};
    transform: ${props => props.isOpen ?
        trans.spin315 : trans.spin0};


&:before {
    width: 100%;
    height: 100%;
    position: absolute;
    transition: top .2s ease-out, transform .22s cubic-bezier(.215,.61,.355,1) .12s;
    top: ${props => props.isOpen ?
        0 : '-1.6rem'} ;
    content: '';
    transform: ${props => props.isOpen ?
        trans.spin90 : trans.spin0};
    border-bottom:3px solid ${props => props.theme.secondary};

}

&:after {
    border-bottom: 3px solid ${props => props.theme.secondary};
    width: 100%;
    height: 100%;
    position: absolute;
    content: '';
    transition: top .2s ease-out;
    top: ${props => props.isOpen ?
        0 : '1.6rem'};

}`

;


export const HiddenNavBG = styled.div`
    background-color: ${props => props.theme.primary}; 
    position: absolute;
    top: -3rem;
    left: 8rem;
    width: 5rem;
    height: 5rem;
    border-radius: 50%;
    transition: transform .8s ease-in-out;
    transform: ${props => props.isOpen ?
        trans.scale100 : trans.scale0};
`;


export const HiddenContainer = styled.div`
        border: 10px solid white;
    position: absolute;
    right: -20rem;
    top: -5rem;
    display:flex;
    align-items: flex-start;
    justify-content: flex-start;
    flex-flow: column;
    padding: 2rem;
    transition: all .8s ease-in-out;
    opacity: ${props => props.isOpen ? 
    '1' : '0'};
    transform: ${props => props.isOpen ?
        null : 'translateX(-250rem)'};
    min-width: 99vw;
    min-height: 99vh;
         @media only screen 
        and (max-device-width: 768px) 
        and (-webkit-min-device-pixel-ratio: 2) { 
    min-height: 27vh;                 

}



    .nav_container-1{
        width: 100%;
        border: 10px solid white;
        height: 100%;
        display: flex;
        flex-flow: row;
        flex: 1 1 auto;
        align-items: flex-start;
         @media only screen 
        and (max-device-width: 768px) 
        and (-webkit-min-device-pixel-ratio: 2) { 
        margin-top: 0;             
}
    }


    .Nav-title{
    } 

    .nav_box-1{
        width: 100%;
        max-height: 20rem;
        flex: 1 1 auto;
        border: 10px solid white;
        /* padding: 2rem;
        margin: 2rem; */
        /* background-color: orangered; */
        display: flex;
        flex-flow: column;
        justify-content: flex-start;
        align-items: flex-start;
         @media only screen 
        and (max-device-width: 768px) 
        and (-webkit-min-device-pixel-ratio: 2) { 
       padding: 0;
       margin: 0;            
}
    }
    .nav_box-2{
        max-height: 70rem;

        flex: 1 1 auto;
        border: 10px solid white;
        padding: 2rem;
        margin: 2rem;
        display: flex;
        flex-flow: column;
        justify-content: center;
        align-items: flex-start;
    }
    .nav_box-3{

        border: 10px solid white;
        min-height: 55rem;
        /* min-height: 85rem; */
        width: 25rem;
        flex: 1 1 auto;
        margin: 2rem;
         display: flex;
        flex-flow: column;
        align-items: center;
        justify-content: space-between;
    }


`;
