import React from 'react'
import styled from 'styled-components';
import MapContainer from './GoogleMaps';

const FooterBar = styled.div`
    color: ${props => props.theme.white};
    background: ${props => props.theme.primary};
    min-width: 100%;
    min-height: 10rem;
    display: flex;
    justify-content: space-between;
    align-items: flex-end;
    position: absolute;
    bottom: 0;
    padding: 2rem;

    .ftr_container-1{
        padding: .2rem;
        min-width: 30rem;

        /* border: 1px solid black; */

        /* background-color:salmon; */

        display: flex;
        flex-flow: column;
        /* align-items: center; */
    }
    .ftr_container-2{
        padding: .2rem;
        min-width: 20rem;
        min-height: 20rem;
        /* border: 1px solid black; */

        /* background-color:orangered; */

        display: flex;
        flex-flow: column;
        justify-content: space-between;
        align-items: center;
    }
    .ftr_container-3{
        /* padding: .2rem;
        min-width: 15rem; */
        /* border: 1px solid black; */

        /* background-color:goldenrod; */

        display: flex;
        flex-flow: column;
        align-items: center;

    }
    .ftr_newsletter-box{
        padding: 1rem;
        min-width: 40rem;
        /* border: 1px solid black; */

        /* background-color:steelblue; */

        display: flex;
        flex: 1;


    }
    .ftr_container-5{
        padding: 1rem;
        min-width: 40rem;
        border: 1px solid white;

        /* background-color: limegreen; */
        min-height: 20rem;

        display: flex;
        flex-flow: column;
    }
    .ftr_sponsor-box{
        padding: 1rem;
        min-width: 40rem;
        border: 1px solid white;

        /* background-color: hotpink; */
        min-height: 25rem;

        display: flex;
        flex-flow: column wrap;
    }
    .ftr_container-7{
        display: flex;
        position: relative;

    }

    .social{
        color: ${props => props.theme.white};;
        margin: 1rem;
        font-size: 2rem;

    }

    a{
        color: ${props => props.theme.white};
        text-transform: uppercase;
        font-family: ${props => props.theme.MPBold};
        font-size: 2rem;
    }

    h1{
        padding: 1rem;
        text-align: center;
    }

    input{
        min-width: 80%;
        margin-left: auto;
    }

    label{
        font-family: ${props => props.theme.MPBold};
        font-size: 2rem;
        text-transform: uppercase;
    }


`;

function Footer() {
    return (
        <FooterBar>
            <div className="ftr_container-1">
                <h1>CONFIRMED EUROBRAKE 2020 SPONSORS</h1>
                <div className="ftr_sponsor-box">SPONSOR BOX</div>
            </div>
            <div className="ftr_container-2">
                <a href="https://www.eurobrake.net/privacy">Privacy Policy</a>
                <a href="https://www.eurobrake.net/disclaimer">Disclaimer</a>
                <a href="">Contact Us</a>
                {/* <a href="">Site Map</a>
                <a href="">links</a> */}
            <div className="ftr_container-7">
                <a className="social" href="http://www.linkedin.com/groups/EuroBrake-4282205?trk=myg_ugrp_ovr">
                <img src="/images/Linkedin@2000x.png" alt="Linkedin" style={{width: '5rem'}}/>
                </a>
                <a  className="social" href="http://www.facebook.com/pages/FISITA-International-Federation-of-Automotive-Engineering-Societies/123022704532">
                    <img src="/images/Facebook@2000x.png" alt="Facebook" style={{width: '5rem'}}/>
                </a>
                <a  className="social" href="http://twitter.com/FISITAhq">
                    <img src="/images/Twitter@2000x.png" alt="Twitter" style={{width: '5rem'}}/>
                </a>
                <a  className="social" href="https://www.flickr.com/photos/eurobrake/albums/72157681368290774">
                    <img src="/images/Facebook@2000x.png" alt="Flickr" style={{width: '5rem'}}/>
                </a>
            </div>
            </div>
            <div className="ftr_container-3">

            <div className="ftr_newsletter-box">
                <label htmlFor="Newsletter">Newsletter</label>
                <input type="email" name="Newsletter" id=""/>    
            </div>
            <div className="ftr_container-7">
              <MapContainer/>
            </div> 
            </div>
        </FooterBar>
    )
}

export default Footer;

