import React, { Component, useState, useEffect } from 'react'
import { Formik, Form, Field, FieldArray } from 'formik';
import Link from 'next/link';
import MenuItem from '@material-ui/core/MenuItem';
import Typography from '@material-ui/core/Typography';
import { TextField, Select, RadioGroup, CheckboxWithLabel, Checkbox, SimpleFileUpload } from 'formik-material-ui';
import { Button } from '../Button';
import { titles, countries, Q1, Q2, Q3, Q4, Marketing, Sessions, members, Initiatives } from './FormSelects';
import { EsopSchema } from './FormControl';
import { StyledForm } from './Formstyles';
import {SendFile} from './FormActions';



export const Esop = (props) => {
 const [Toggle, setToggle] = useState(false);
    const [Loading, setLoading] = useState(false);
    const [Status, setStatus] = useState(null);
    const { presets, csrf, apiUrl} = props;
    const url = `https://prelude.eurobrake.net/esop`;

    return (
        <Formik
            initialValues={emptyInitial || presets}
            validationSchema={EsopSchema}
            enableReinitialize
        >
            {({ values, handleChange, setFieldTouched, isValidating, validateForm, handleSubmit, errors, isSubmitting }) => {


                    const allTouched = async () => {
                     await Object.keys(values).forEach(key => {   
                        setFieldTouched(key, true)});
                        console.log({errors})
                       await validateForm().then(errors => Object.keys(errors).length === 0 && onSubmit())
                }

                const onSubmit = async () => {
                 await setLoading(true);
                values.__csrf_token = csrf
                  await  console.log('submitting')
                  let res =  await SendFile({values, url, csrf});
                  let data = await  res;
                  let dataStatus= await  data && data.status;
                  setStatus(dataStatus)
                  console.log({data})
                  let result = async () => {
                  await  setLoading(false)
                  await setToggle(true)
                   }    

                return result()

              }


                return (
                    <StyledForm>
                        <Typography className="form-title" style={{marginTop: '4rem'}}>About you</Typography>
                        <div className="form-field">
                            <label
                                htmlFor="label"
                                className="form-label">
                                Title
                        </label>
                            <Field
                                className="form-input"
                                onClick={handleChange}
                                value={values.student_title}
                                style={{ color: '#134381' }}
                                name="student_title"
                                component={Select}
                            >
                                {titles.map((option, i) =>
                                    <MenuItem key={i} style={{ fontSize: '1.5rem', cursor: 'pointer' }} value={option.value}>{option.label}</MenuItem>)}
                            </Field>
                        </div>
                        <div className="form-field">
                            <label
                                htmlFor="label"
                                className="form-label">
                                First Name:
                        </label>
                            <Field
                                className="form-input"
                                onClick={handleChange}
                                value={values.student_firstname}
                                style={{ color: '#134381' }}
                                name="student_firstname"
                                component={TextField}
                            />
                        </div>
                        <div className="form-field">
                            <label
                                htmlFor="label"
                                className="form-label">
                                Last Name:
                        </label>
                            <Field
                                className="form-input"
                                onClick={handleChange}
                                value={values.student_lastname}
                                style={{ color: '#134381' }}
                                name="student_lastname"
                                component={TextField}
                            />
                        </div>
                        <div className="form-field">
                            <label
                                htmlFor="label"
                                className="form-label">
                                Email:
                        </label>
                            <Field
                                className="form-input"
                                onClick={handleChange}
                                value={values.student_email}
                                style={{ color: '#134381' }}
                                name="student_email"
                                component={TextField}
                            />
                        </div>
                        <div className="form-field">
                            <label
                                htmlFor="label"
                                className="form-label">
                                Address:
                        </label>
                            <Field
                                className="form-input"
                                onClick={handleChange}
                                value={values.student_address1}
                                style={{ color: '#134381' }}
                                name="student_address1"
                                component={TextField}
                            />
                        </div>
                        <div className="form-field">
                            <label
                                htmlFor="label"
                                className="form-label">

                            </label>
                            <Field
                                className="form-input"
                                onClick={handleChange}
                                value={values.student_address2}
                                style={{ color: '#134381' }}
                                name="student_address2"
                                component={TextField}
                            />
                        </div>
                        <div className="form-field">
                            <label
                                htmlFor="label"
                                className="form-label">

                            </label>
                            <Field
                                className="form-input"
                                onClick={handleChange}
                                value={values.student_address3}
                                style={{ color: '#134381' }}
                                name="student_address3"
                                component={TextField}
                            />
                        </div>
                        <div className="form-field">
                            <label
                                htmlFor="label"
                                className="form-label">
                                City:
                        </label>
                            <Field
                                className="form-input"
                                onClick={handleChange}
                                value={values.student_city}
                                style={{ color: '#134381' }}
                                name="student_city"
                                component={TextField}
                            />
                        </div>
                        <div className="form-field">
                            <label
                                htmlFor="label"
                                className="form-label">
                                State/ County/ Province:
                        </label>
                            <Field
                                className="form-input"
                                onClick={handleChange}
                                value={values.student_scp}
                                style={{ color: '#134381' }}
                                name="student_scp"
                                component={TextField}
                            />
                        </div>
                        <div className="form-field">
                            <label
                                htmlFor="label"
                                className="form-label">
                                Postal/ Zip code:
                        </label>
                            <Field
                                className="form-input"
                                onClick={handleChange}
                                value={values.student_postal}
                                style={{ color: '#134381' }}
                                name="student_postal"
                                component={TextField}
                            />
                        </div>
                        <div className="form-field">
                            <label
                                htmlFor="label"
                                className="form-label">
                                Country
                        </label>
                            <Field
                                className="form-input"
                                onClick={handleChange}
                                value={values.student_country}
                                style={{ color: '#134381' }}
                                name="student_country"
                                component={Select}
                            >
                                {countries.map((option, i) =>
                                    <MenuItem key={i} style={{ fontSize: '1.5rem', cursor: 'pointer' }} value={option.value}>{option.label}</MenuItem>)}
                            </Field>
                        </div>
                        <div className="form-field">
                            <label
                                htmlFor="label"
                                className="form-label">
                                Your FISITA member society
                        </label>
                            <Field
                                className="form-input"
                                onClick={handleChange}
                                value={values.student_country}
                                style={{ color: '#134381' }}
                                name="student_membersociety"
                                component={Select}
                            >
                                {members.map((option, i) =>
                                    <MenuItem key={i} style={{ fontSize: '1.5rem', cursor: 'pointer' }} value={option.value}>{option.label}</MenuItem>)}
                            </Field>
                        </div>
                        <div className="form-field">
                            <label
                                htmlFor="label"
                                className="form-label">
                                Place of birth
                        </label>
                            <div className="form-input" >
                                <Field
                                    placeholder="City"
                                    className="form-input"
                                    onClick={handleChange}
                                    value={values.student_jobtitle}
                                    style={{ color: '#134381', marginBottom: '2rem', width: '100%' }}
                                    name="birth_city"
                                    component={TextField}
                                />
                                <Field
                                    placeholder="Country"
                                    className="form-input"
                                    onClick={handleChange}
                                    value={values.student_jobtitle}
                                    style={{ color: '#134381', width: '100%' }}
                                    name="birth_country"
                                    component={TextField}
                                />
                            </div>
                        </div>
                        <Typography className="form-title" style={{marginTop: '4rem'}}>Your University or HE Institution</Typography>
                        <div className="form-field-radio">
                            <label
                                htmlFor="label"
                                className="form-label-radio">
                                What level of education have you achieved?
                        </label>
                            <Field
                                className="form-radio"
                                value={values.student_level_current}
                                style={{ color: '#134381' }}
                                name="student_level_current"
                                component={RadioGroup}
                            >
                                <label
                                    style={{ margin: '1rem' }}
                                    htmlFor="label"
                                    className="form-label">
                                        <input
                                        onChange={handleChange}
                                        style={{ margin: '1rem' }}
                                        type="radio"
                                        name="student_level_current"
                                        value="none"
                                        id="student_level_current_none"
                                    />
                                    None
                                </label>
                                <label
                                    style={{ margin: '1rem' }}
                                    htmlFor="label"
                                    className="form-label">
                                        <input
                                        style={{ margin: '1rem' }}
                                        onChange={handleChange}
                                        type="radio"
                                        name="student_level_current"
                                        value="Bachelor"
                                        id="student_level_current_Bachelor"
