import styled from 'styled-components';
import fetch from 'isomorphic-unfetch';



const DemoDiv = styled.form`
    display: flex;
    flex-direction: column;
    align-self: center; 
    max-width: ${props => props.theme.maxWidth};
`;


const Demo = styled.h1`
    padding: 10rem;
    margin: 5rem;
    color: orangered;

`;



export default class Login extends Component {
    static getInitialProps = async function () {
        const res = await fetch('https://prelude.eurobrake.net/login');
        const data = await res.json().catch(error => console.log(error));;
        return {
            form: data
        };
    };


constructor(props) {
    super(props);
    this.state={
        username: '',
        password: '',
        authorLogin: true,
        error: ''
    }
}

    saveToState = (e) => {
        this.setState({ [e.target.name]: e.target.value });
    }
    onSub =(e) => {
    e.preventDefault()
        console.log('click1');
    }

    onSubmit = async (e) =>{
    e.preventDefault()
    console.log('click');

    const {authorLogin, uesername, password, error} = this.state;
    const apiUrl = authorLogin ? 'https://prelude.eurobrake.net/login' : '' ;
// action="//2019.eurobrake.net/exhibition/exhibitors/login" SHOULD BE LOGIN FORM FOR EXHIBITORS

        const formData = {
            username: username,
            password: password,

        }
    try{

        const response = await fetch(apiUrl, {
                method: 'POST',
                credentials: 'include',
                body: JSON.stringify(formData)
            });

        if(response.ok) {
        const data = await response.json()
        return {
            loggedIn: data
            }
    }else{
        console.log('login failed')
        let error = new Error(response.statusText)
        error.response = response
        return Promise.reject(error)
        }
    }catch (error){
        console.error(
            'Failed to login, please try again', error
            )
        throw new Error(error)
    }

}


render(){
  console.log(this.props)
    const {form} = this.props.form;
