import React from 'react';
import styled from 'styled-components';
import Typography from '@material-ui/core/Typography';
import CountDownBanner from '../components/CountDownBanner';


const StyledHero = styled.div`
    height: 100vh;  
    display: flex;
    flex-flow: column;

    img{
        flex: 1;
        object-fit: cover;
    }

    .heading{
        align-self: center;
        max-width: ${props => props.maxWidth || props.theme.maxWidth};
        position: absolute;
        line-height: 1;
        top: ${props => props.top || '20rem'};
        font-size: 11rem;
        font-family:${props => props.theme.MPBlack};
        color: ${props => props.theme.white};
        text-align: left;
        text-shadow: 0px 3px 50px #0000008C;
    }
`;

export default function HeroSection(props) {
    return (
        <StyledHero>
            <img src={props.src ? props.src : "/images/pic1.png"} alt="EuroBrake Greating"  />
            <Typography className="heading">
                {props.children}
            </Typography>
            {props.banner ? <props.banner/>: <CountDownBanner />}
        </StyledHero>
    )
}
