import React, { Component } from 'react'
import styled from 'styled-components';
import makeCarousel from 'react-reveal/makeCarousel';
import Slide from 'react-reveal/Slide';
import CountDownBanner from './CountDownBanner';

const IndexHero = styled.div`
min-width: 100%;
min-height: ${props => props.theme.heroHeightBig};
  @media only screen 
        and (max-device-width: 768px) 
        and (-webkit-min-device-pixel-ratio: 2) {
    min-height: ${props => props.h || props.theme.heroHeightSml };
    }
display: flex;
flex-flow: column;

.banner{
        display: flex;
        flex: 1 1 20%;
    }

`;

const Container = styled.div`
position: relative;
overflow: hidden;
flex: 2 1 80%;

`;

const StyledSlide = styled.div` 
display: flex;
object-fit: cover;
/* flex: 1 1 auto;  */
 img{
flex: 1;
object-fit: cover;

  }
`;



const CarouselUI = ({ children }) => <Container>{children}</Container>;
const Carousel = makeCarousel(CarouselUI);

export default function StyledCarousel() {
    return (
        <IndexHero>
        <Carousel defaultWait={4000} /*wait for 1000 milliseconds*/>
            <Slide right style={{}}>
                <StyledSlide>

                    <img srcSet="/images/pic11.png" alt="EuroBrake Greating"/>
                </StyledSlide>
            </Slide>
            <Slide right>
                <StyledSlide>

                    <img srcSet="/images/pic2.png" alt="EuroBrake Greating"/>
                </StyledSlide>
            </Slide>
        </Carousel>
        <CountDownBanner />
        </IndexHero>
    )
}
