        this.setState({
            [name]: value
        });
    }

    render() {
        return (
            <>
                <HeroSection banner={FilledBanner}>
                    Edit Your Abstract
            </HeroSection>
                <StyledPage>
                    <div className="authors">
                        <Typography className="authors-title">Hi {'*USERNAME*'}</Typography>
                        <Typography gutterBottom className="authors_sub-title">Edit Your Abstract</Typography>
                    </div>
                </StyledPage>
            </>
        )
    }
}

