import React, { Component } from 'react'
import Router from 'next/router';
import cookies from 'next-cookies';
import cookie from 'js-cookie';
import fetch from 'isomorphic-unfetch';
import styled from 'styled-components';
import Typography from '@material-ui/core/Typography';
import ExpandMoreOutlinedIcon from '@material-ui/icons/ExpandMoreOutlined';
import CreateIcon from '@material-ui/icons/Create';
import AssignmentIcon from '@material-ui/icons/Assignment';
import ExitToAppIcon from '@material-ui/icons/ExitToApp';
import CancelIcon from '@material-ui/icons/Cancel';
import { withAuthSync, logout, login } from '../utils/auth'
import HeroSection from '../components/HeroSection';
import { Button } from '../components/Button';
import { StyledBanner, StyledContainer} from '../components/styles/PageStyles';



const StyledPage = styled.div`
    display: flex;
    flex-direction: column;
    align-self: center;
    max-width: ${props => props.theme.maxWidth};
    background-color: ${props => props.theme.white};
    margin-top: 7rem;

    .authors{
        color: ${props => props.theme.primary};
        font-family: ${props => props.theme.MPLight};
        font-size: 2rem;

    &-title{
        color: ${props => props.theme.primary};
        font-family: ${props => props.theme.MPSemibold};
        font-size: 4.4rem;
        margin-top: 3rem;

    }

    &_sub-title{
        color: ${props => props.theme.primary};
        font-size: 3.5rem;
        font-family: ${props => props.theme.MPLightIt};
        padding-bottom: .5rem;

    }
    &-bold{
        font-family: ${props => props.theme.MPBold};
    }
    &-it{
        font-family: ${props => props.theme.MPLightIt};
    }
    &_action-box{
        display: flex;
        justify-content: space-between;
    }
}
`;



const FilledBanner = () => (

    <StyledBanner>
        <StyledContainer >

            <Typography className="banner-heading"  >Welcome to the Author's Area</Typography>
            <Typography className="banner-sub" sfs="3rem">Submit your review ready papers by 26 January 2020</Typography>
            <div className="banner_bottom">
                <ExpandMoreOutlinedIcon className="banner_bottom-icon" />
                <Typography varient="h3" className="banner_bottom-details">Submit your review ready papers by 26 January 2020</Typography>
            </div>
        </StyledContainer>
    </StyledBanner>
)

class AuthorsAreaDash extends Component {
    constructor(props) {
        super(props);
        this.state = {
            hasForm: false,
            formData: undefined,
            error: '',
            userLoggedIn: false,
        }


    }

    componentDidMount = () => {
        const { userLoggedIn } = this.state;
        const { logintoken } = cookie.get();
        console.log('cdm', logintoken)
        logintoken && !userLoggedIn ? this.setState(prev => ({
            userLoggedIn: !prev
        })) : null
    }

    getAbstractForm = async (e) => {
        e.preventDefault()
        console.log('click');
        const { logintoken } = cookie.get();
        console.log(logintoken)
        try {
            const apiUrl = 'https://prelude.eurobrake.net/submit';
            const response = await fetch(apiUrl, {
                credentials: 'include',
                headers: {
                    Authorization: `Bearer ${logintoken}`,
                }
            });
            const data = await response.json();
            if (data.status === 'success') {
                this.setState(prev => (
                    {
                        hasForm: !prev,
                        formData: data.form
                    }));
                // console.log(data.form);
                // console.log('stringify',JSON.stringify(data.form));
                console.log(data)


            } else {
                let error = new Error(data.error)
                error.response = response
                throw error
            }
        } catch (error) {
            console.error(
                'Failed to get form, please try again', error
            )
            this.setState({ error: error.message });
        }
    }


    handleInputChange = (event) => {
        const target = event.target;
        const value = target.type === 'checkbox' ? target.checked : target.value;
        const name = target.name;

        this.setState({
            [name]: value
        });
    }

    render() {
        // const {name} = this.props.authorData;
        const { userLoggedIn, hasForm } = this.state;
        console.log({ userLoggedIn, hasForm });
        return (
            <>
                <HeroSection banner={FilledBanner}>
                    Author's Area Dashboard
            </HeroSection>
                <StyledPage>
                    <div className="authors">

                    <Typography className="authors-title">Hi {'*USERNAME*'}</Typography>
                    <Typography gutterBottom className="authors_sub-title">Welcome to the Author's Area</Typography>
                    <div className="authors_action-box">
                    <Button
                        bcolor="#134381"
                        background="#134381"
                        br="100rem"
                        padding="0.5rem 2rem"
                        style={{ margin: ".5rem" }}
                        fs="1.5rem"
                        onClick={this.getAbstractForm}>
                        <CreateIcon style={{fontSize: '3rem', marginRight: '1rem'}}/>
                        EDIT PROFILE
                    </Button>
                    <Button
                        bcolor="#134381"
                        padding="0.5rem 2rem"
                        background="#134381"
                        br="100rem"
                        style={{ margin: ".5rem" }}
                        fs="1.5rem"
                        onClick={this.getAbstractForm}>
                        <AssignmentIcon style={{fontSize: '3rem', marginRight: '.5rem'}}/>
                        INSTRUCTIONS & TEMPLATES
                    </Button>
                    <Button
                        bcolor="#134381"
                        padding="0.5rem 2rem"
                        background="#134381"
                        br="100rem"
                        style={{ margin: ".5rem" }}
                        fs="1.5rem"
                        onClick={logout}>
                        <ExitToAppIcon style={{fontSize: '3rem', marginRight: '.5rem'}}/>
                        logout
                    </Button>
                    </div>
                    <Typography gutterBottom className="authors-title">Your Abstracts</Typography>

                    </div>
                </StyledPage>
            </>

        )

    }
}

