import * as yup from 'yup';
export const PaperSchema = yup.object().shape({
  accept: yup.bool().test('accept', 'You have to agree with our Terms and Conditions!', value => value === true),
  technicalpaper_filename: yup.string().required('Required'),
  author_notes: yup.string().max(3, 'too many words').required('Required'),
  copyright: yup.bool().oneOf([true], 'Field must be checked')
